const MANIFEST = {
  "type": "Interactive.MagneticButton",
  "description": "Button with magnetic hover effect that follows cursor",
  "editorElement": {
    "selector": ".magnetic-button",
    "displayName": "Magnetic Button",
    "archetype": "container",
    "data": {
      "buttonText": {
        "dataType": "text",
        "displayName": "Button Text",
        "defaultValue": "Hover Me",
        "group": "Content"
      },
      "backgroundColor": {
        "dataType": "color",
        "displayName": "Background Color",
        "defaultValue": "#1C1917",
        "group": "Colors"
      },
      "textColor": {
        "dataType": "color",
        "displayName": "Text Color",
        "defaultValue": "#FFFFFF",
        "group": "Colors"
      },
      "magneticStrength": {
        "dataType": "select",
        "displayName": "Magnetic Strength",
        "defaultValue": "medium",
        "options": ["low", "medium", "high"],
        "group": "Animation"
      }
    }
  }
};

function Component({ config = {} }) {
  const buttonRef = React.useRef(null);
  const [position, setPosition] = React.useState({ x: 0, y: 0 });
  const [isHovering, setIsHovering] = React.useState(false);

  const strength = {
    low: 0.2,
    medium: 0.4,
    high: 0.6
  }[config?.magneticStrength || 'medium'];

  const handleMouseMove = (e) => {
    if (!buttonRef.current) return;
    const rect = buttonRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const deltaX = (e.clientX - centerX) * strength;
    const deltaY = (e.clientY - centerY) * strength;
    setPosition({ x: deltaX, y: deltaY });
  };

  const handleMouseLeave = () => {
    setPosition({ x: 0, y: 0 });
    setIsHovering(false);
  };

  return (
    <div style={{
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      backgroundColor: '#FAFAFA'
    }}>
      <button
        ref={buttonRef}
        onMouseMove={handleMouseMove}
        onMouseEnter={() => setIsHovering(true)}
        onMouseLeave={handleMouseLeave}
        style={{
          padding: '20px 40px',
          fontSize: '18px',
          fontWeight: '500',
          backgroundColor: config?.backgroundColor || '#1C1917',
          color: config?.textColor || '#FFFFFF',
          border: 'none',
          borderRadius: '8px',
          cursor: 'pointer',
          transform: `translate(${position.x}px, ${position.y}px)`,
          transition: isHovering ? 'transform 0.2s ease-out' : 'transform 0.4s ease-out',
          boxShadow: isHovering ? '0 8px 24px rgba(0,0,0,0.2)' : '0 4px 12px rgba(0,0,0,0.1)'
        }}
      >
        {config?.buttonText || 'Hover Me'}
      </button>
    </div>
  );
}

