      },
      "radius": {
        "dataType": "select",
        "displayName": "Circle Radius",
        "defaultValue": "280",
        "options": ["200", "240", "280", "320", "360"],
        "group": "Layout"
      }
    }
  }
};

function Component({ config = {} }) {
  const items = (config?.items || 'HOME,ABOUT,SERVICES,WORK,CONTACT').split(',').map(item => item.trim());
  const radius = parseInt(config?.radius || '280');
  const angle = 360 / items.length;

  const MenuItem = ({ item, index }) => {
    const [offset, setOffset] = React.useState({ x: 0, y: 0 });
    const itemRef = React.useRef(null);

    const handleMouseMove = (e) => {
      if (!itemRef.current) return;
      const rect = itemRef.current.getBoundingClientRect();
      const centerX = rect.left + rect.width / 2;
      const centerY = rect.top + rect.height / 2;
      const deltaX = (e.clientX - centerX) * 0.3;
      const deltaY = (e.clientY - centerY) * 0.3;
      setOffset({ x: deltaX, y: deltaY });
    };

    const itemAngle = angle * index;

    return (
      <div
        ref={itemRef}
        onMouseMove={handleMouseMove}
        onMouseLeave={() => setOffset({ x: 0, y: 0 })}
        style={{
          position: 'absolute',
          left: '50%',
          top: '50%',
          transform: `rotate(${itemAngle}deg) translateX(${radius}px) rotate(-${itemAngle}deg) translate(${offset.x}px, ${offset.y}px)`,
          transformOrigin: '0px 0px',
          color: config?.textColor || '#FFFFFF',
          fontSize: '18px',
          fontWeight: '500',
          cursor: 'pointer',
          transition: 'transform 0.3s ease-out',
          whiteSpace: 'nowrap'
        }}
      >
        {item}
      </div>
    );
  };

  return (
    <div style={{
      backgroundColor: config?.backgroundColor || '#000000',
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    }}>
      <div style={{
        position: 'relative',
        width: radius * 2 + 200,
        height: radius * 2 + 200
      }}>
        {items.map((item, index) => (
          <MenuItem key={index} item={item} index={index} />
        ))}
      </div>
    </div>
  );
}

