      },
      "textColor": {
        "dataType": "color",
        "displayName": "Text Color",
        "defaultValue": "#A1A1AA",
        "group": "Colors"
      },
      "activeTextColor": {
        "dataType": "color",
        "displayName": "Active Text Color",
        "defaultValue": "#FAFAFA",
        "group": "Colors"
      },
      "gradientColor1": {
        "dataType": "color",
        "displayName": "Gradient Color 1",
        "defaultValue": "#27272A",
        "group": "Colors"
      },
      "gradientColor2": {
        "dataType": "color",
        "displayName": "Gradient Color 2",
        "defaultValue": "#3F3F46",
        "group": "Colors"
      },
      "fontSize": {
        "dataType": "number",
        "displayName": "Font Size (px)",
        "defaultValue": 16,
        "group": "Typography"
      },
      "fontWeight": {
        "dataType": "select",
        "displayName": "Font Weight",
        "defaultValue": "400",
        "options": ["300", "400", "500"],
