  };

  const initializeGrid = () => {
    const grid = [];
    for (let i = 0; i < gridSize; i++) {
      grid[i] = [];
      for (let j = 0; j < gridSize; j++) {
        grid[i][j] = {a: Math.random(), b: Math.random()};
      }
    }
    const cx = Math.floor(gridSize / 2);
