      for (let j = 0; j < gridSize; j++) {
        const a = grid[i][j].a;
        const b = grid[i][j].b;

        let laplaceA = 0;
        let laplaceB = 0;

        const neighbors = [
          [i-1,j], [i+1,j], [i,j-1], [i,j+1]
