function Component({ config = {} }) {
  const [rotationAngle, setRotationAngle] = React.useState(0);

  const animationFrameRef = React.useRef(null);
  const startTimeRef = React.useRef(Date.now());

  const prefersReducedMotion = typeof window !== 'undefined' 
    ? window.matchMedia('(prefers-reduced-motion: reduce)').matches 
    : false;

  // Extract config
  const strand1Text = config?.strand1Text || "EVOLUTION";
  const strand2Text = config?.strand2Text || "CREATION";
  const backgroundColor = config?.backgroundColor || "#F1F3F5";
  const strand1Color = config?.strand1Color || "#212529";
  const strand2Color = config?.strand2Color || "#495057";
  const connectionColor = config?.connectionColor || "#DEE2E6";
  const fontSize = parseInt(config?.fontSize) || 40;
  const fontWeight = config?.fontWeight || "400";
  const helixRadius = parseFloat(config?.helixRadius) || 120;
  const helixPitch = parseFloat(config?.helixPitch) || 400;
  const rotationSpeed = parseFloat(config?.rotationSpeed) || 0.3;
  const depthScale = parseFloat(config?.depthScale) || 0.5;
  const showConnections = config?.showConnections !== false;
  const autoRotate = config?.autoRotate !== false;

  // Use longer strand for positioning
  const maxLength = Math.max(strand1Text.length, strand2Text.length);
  const strand1Chars = strand1Text.split('');
  const strand2Chars = strand2Text.split('');

  // Calculate helix position for a character
  const calculateHelixPosition = (index, totalChars, strandOffset, rotation) => {
    // Parametric helix equations
    const t = (index / totalChars) * Math.PI * 4; // 2 full turns
    const z = (index / totalChars) * helixPitch;

    // Add rotation and strand offset (180Â° for opposite strand)
    const angle = t + rotation + strandOffset;

    const x = Math.cos(angle) * helixRadius;
    const y = Math.sin(angle) * helixRadius;

    // Calculate scale based on z-depth (perspective)
    const scale = 1 - (Math.abs(y) / helixRadius) * depthScale;

    // Calculate opacity based on depth
    const opacity = 0.3 + (scale * 0.7);

    // Calculate rotation to face outward
    const faceRotation = (angle * 180 / Math.PI) + 90;

    return { x, y, z, scale, opacity, faceRotation };
  };

  // Auto-rotation animation
  React.useEffect(() => {
    if (!autoRotate || prefersReducedMotion) return;

    const animate = () => {
      const elapsed = (Date.now() - startTimeRef.current) * 0.001;
      setRotationAngle(elapsed * rotationSpeed);
      animationFrameRef.current = requestAnimationFrame(animate);
    };

    animationFrameRef.current = requestAnimationFrame(animate);

    return () => {
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, [autoRotate, rotationSpeed, prefersReducedMotion]);

  const containerStyle = {
    minHeight: '100vh',
    backgroundColor,
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    position: 'relative',
    overflow: 'hidden',
    perspective: '1500px',
    transformStyle: 'preserve-3d'
  };

  const helixContainerStyle = {
    position: 'relative',
    width: `${helixRadius * 3}px`,
    height: `${helixPitch + 100}px`,
    transformStyle: 'preserve-3d'
  };

  const characterStyle = (position, color) => {
    if (prefersReducedMotion) {
      return {
        position: 'absolute',
        left: '50%',
        top: `${position.z}px`,
        fontSize: `${fontSize}px`,
        fontWeight,
        color,
        transform: 'translate(-50%, -50%)',
        userSelect: 'none',
        pointerEvents: 'none',
        opacity: position.opacity
      };
    }

    // Sort by y (depth) for proper layering
    const zIndex = Math.floor((1 - position.opacity) * 100);

    return {
      position: 'absolute',
      left: `calc(50% + ${position.x}px)`,
      top: `${position.z}px`,
      fontSize: `${fontSize * position.scale}px`,
      fontWeight,
      color,
      transform: `translate(-50%, -50%) rotateZ(${position.faceRotation}deg)`,
      opacity: position.opacity,
      userSelect: 'none',
      pointerEvents: 'none',
      transformStyle: 'preserve-3d',
      zIndex,
      willChange: 'transform, opacity',
      transition: 'none',
      textShadow: `0 0 ${fontSize * position.scale * 0.2}px ${color}40`
    };
  };

  const connectionLineStyle = (pos1, pos2) => {
    const dx = pos2.x - pos1.x;
    const dy = pos2.z - pos1.z;
    const length = Math.sqrt(dx * dx + dy * dy);
    const angle = Math.atan2(dy, dx) * 180 / Math.PI;

    const averageOpacity = (pos1.opacity + pos2.opacity) / 2;
    const averageScale = (pos1.scale + pos2.scale) / 2;

    return {
      position: 'absolute',
      left: `calc(50% + ${pos1.x}px)`,
      top: `${pos1.z}px`,
      width: `${length}px`,
      height: '1px',
      backgroundColor: connectionColor,
      transformOrigin: 'left center',
      transform: `rotate(${angle}deg)`,
      opacity: averageOpacity * 0.5,
      pointerEvents: 'none',
      zIndex: 0
    };
  };

  const infoStyle = {
    position: 'fixed',
    bottom: '2rem',
    left: '50%',
    transform: 'translateX(-50%)',
    fontSize: '14px',
    color: strand1Color,
    opacity: 0.4,
    zIndex: 100,
    textAlign: 'center',
    pointerEvents: 'none',
    fontWeight: '300'
  };

  // Calculate positions for all characters
  const strand1Positions = strand1Chars.map((char, index) => ({
    char,
    ...calculateHelixPosition(index, maxLength, 0, rotationAngle)
  }));

  const strand2Positions = strand2Chars.map((char, index) => ({
    char,
    ...calculateHelixPosition(index, maxLength, Math.PI, rotationAngle)
  }));

  // Sort all characters by opacity for proper z-ordering
  const allCharacters = [
    ...strand1Positions.map((pos, i) => ({ ...pos, color: strand1Color, index: i, strand: 1 })),
    ...strand2Positions.map((pos, i) => ({ ...pos, color: strand2Color, index: i, strand: 2 }))
  ].sort((a, b) => a.opacity - b.opacity);

  return (
    <div style={containerStyle} className="dna-helix-text">
      <div style={helixContainerStyle}>
        {/* Base-pair connections */}
        {showConnections && !prefersReducedMotion && strand1Positions.map((pos1, index) => {
          if (index < strand2Positions.length) {
            const pos2 = strand2Positions[index];
            return (
              <div key={`conn-${index}`} style={connectionLineStyle(pos1, pos2)} />
            );
          }
          return null;
        })}

        {/* Characters (sorted by depth) */}
        {allCharacters.map((item, index) => (
          <div key={`${item.strand}-${item.index}`} style={characterStyle(item, item.color)}>
            {item.char === ' ' ? '\u00A0' : item.char}
          </div>
        ))}
      </div>

      {/* Info */}
      <div style={infoStyle}>
        Parametric double helix â€¢ Two strands rotating in opposite directions
      </div>
    </div>
  );
}

const MANIFEST = {"type": "Visualization.DifferentialGrowth", "description": "Differential growth algorithm simulating coral/brain tissue organic expansion", "editorElement": {"selector": ".differential-growth", "displayName": "Differential Growth", "archetype": "container", "data": {"nodeCount": {"dataType": "select", "displayName": "Initial Nodes", "defaultValue": "50", "options": ["30", "40", "50", "60", "80"], "group": "Content"}, "growthRate": {"dataType": "select", "displayName": "Growth Rate", "defaultValue": "0.5", "options": ["0.2", "0.3", "0.5", "0.7", "1.0"], "group": "Animation"}, "maxDist": {"dataType": "select", "displayName": "Max Distance", "defaultValue": "15", "options": ["10", "12", "15", "18", "20"], "group": "Animation"}, "repulsion": {"dataType": "select", "displayName": "Repulsion Force", "defaultValue": "0.5", "options": ["0.2", "0.3", "0.5", "0.7", "1.0"], "group": "Animation"}, "showInfo": {"dataType": "booleanValue", "displayName": "Show Info", "defaultValue": true, "group": "Content"}, "backgroundColor": {"dataType": "color", "displayName": "Background", "defaultValue": "#FFFFFF", "group": "Colors"}, "organismColor": {"dataType": "color", "displayName": "Organism Color", "defaultValue": "#212529", "group": "Colors"}, "textColor": {"dataType": "color", "displayName": "Text Color", "defaultValue": "#495057", "group": "Colors"}, "lineWidth": {"dataType": "select", "displayName": "Line Width", "defaultValue": "2", "options": ["1", "1.5", "2", "2.5", "3"], "group": "Layout"}, "fontSize": {"dataType": "number", "displayName": "Font Size", "defaultValue": 12, "group": "Typography"}, "fontWeight": {"dataType": "select", "displayName": "Font Weight", "defaultValue": "400", "options": ["300", "400", "500"], "group": "Typography"}}, "layout": {"resizeDirection": "horizontalAndVertical", "contentResizeDirection": "vertical"}}};

function Component({ config = {} }) {
  const containerRef = React.useRef(null);
  const canvasRef = React.useRef(null);
  const nodesRef = React.useRef([]);
  const animationRef = React.useRef(null);
  const isPausedRef = React.useRef(false);

  const nodeCount = parseInt(config?.nodeCount || '50');
  const growthRate = parseFloat(config?.growthRate || '0.5');
  const maxDist = parseFloat(config?.maxDist || '15');
  const repulsion = parseFloat(config?.repulsion || '0.5');
  const showInfo = config?.showInfo !== false;
  const backgroundColor = config?.backgroundColor || '#FFFFFF';
  const organismColor = config?.organismColor || '#212529';
  const textColor = config?.textColor || '#495057';
  const lineWidth = parseFloat(config?.lineWidth || '2');
  const fontSize = config?.fontSize || 12;
  const fontWeight = config?.fontWeight || '400';

  const initializeNodes = (width, height) => {
    const nodes = [];
    const cx = width / 2;
    const cy = height / 2;
    const radius = 50;

    for (let i = 0; i < nodeCount; i++) {
      const angle = (i / nodeCount) * Math.PI * 2;
      nodes.push({
        x: cx + Math.cos(angle) * radius,
        y: cy + Math.sin(angle) * radius,
        vx: 0,
        vy: 0
      });
    }
    return nodes;
  };

  const updateNodes = (nodes) => {
    for (let i = 0; i < nodes.length; i++) {
      const node = nodes[i];
      const prev = nodes[(i - 1 + nodes.length) % nodes.length];
      const next = nodes[(i + 1) % nodes.length];

      const dx1 = prev.x - node.x;
      const dy1 = prev.y - node.y;
      const dist1 = Math.sqrt(dx1 * dx1 + dy1 * dy1);

      const dx2 = next.x - node.x;
      const dy2 = next.y - node.y;
      const dist2 = Math.sqrt(dx2 * dx2 + dy2 * dy2);

      if (dist1 > maxDist) {
        const newNode = {
          x: (node.x + prev.x) / 2,
          y: (node.y + prev.y) / 2,
          vx: 0,
          vy: 0
        };
        nodes.splice(i, 0, newNode);
        i++;
      }

      let fx = 0, fy = 0;

      for (let j = 0; j < nodes.length; j++) {
        if (Math.abs(i - j) < 2 || Math.abs(i - j) > nodes.length - 2) continue;

        const other = nodes[j];
        const dx = node.x - other.x;
        const dy = node.y - other.y;
        const distSq = dx * dx + dy * dy;
        const dist = Math.sqrt(distSq);

        if (dist < maxDist * 2 && dist > 0) {
          const force = repulsion / distSq;
          fx += (dx / dist) * force;
          fy += (dy / dist) * force;
        }
      }

      node.vx = (node.vx + fx) * 0.9;
      node.vy = (node.vy + fy) * 0.9;
      node.x += node.vx * growthRate;
      node.y += node.vy * growthRate;
    }

    if (nodes.length > 2000) {
      nodes.splice(Math.floor(nodes.length / 2), 1);
    }
  };

  const render = (canvas, nodes) => {
    if (!canvas) return;
    const ctx = canvas.getContext('2d');

    ctx.fillStyle = backgroundColor;
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    ctx.strokeStyle = organismColor;
    ctx.lineWidth = lineWidth;
    ctx.lineCap = 'round';
    ctx.lineJoin = 'round';

    ctx.beginPath();
    if (nodes.length > 0) {
      ctx.moveTo(nodes[0].x, nodes[0].y);
      for (let i = 1; i < nodes.length; i++) {
        ctx.lineTo(nodes[i].x, nodes[i].y);
      }
      ctx.closePath();
    }
    ctx.stroke();

    ctx.fillStyle = organismColor + '20';
    ctx.fill();
  };

  const animate = () => {
    if (isPausedRef.current) return;
    const canvas = canvasRef.current;
    if (!canvas) return;
    updateNodes(nodesRef.current);
    render(canvas, nodesRef.current);
    animationRef.current = requestAnimationFrame(animate);
  };

  const togglePause = () => {
    isPausedRef.current = !isPausedRef.current;
    if (!isPausedRef.current) animate();
  };

  const handleReset = () => {
    const canvas = canvasRef.current;
    if (canvas) {
      nodesRef.current = initializeNodes(canvas.width, canvas.height);
      render(canvas, nodesRef.current);
    }
  };

  React.useEffect(() => {
    const canvas = canvasRef.current;
    const container = containerRef.current;
    if (!canvas || !container) return;

    const updateSize = () => {
      const rect = container.getBoundingClientRect();
      if (rect.width > 0 && rect.height > 0) {
        canvas.width = rect.width;
        canvas.height = rect.height;
        nodesRef.current = initializeNodes(rect.width, rect.height);
      }
    };

    updateSize();
    const resizeObserver = new ResizeObserver(updateSize);
    resizeObserver.observe(container);
    animate();

    return () => {
      resizeObserver.disconnect();
      if (animationRef.current) cancelAnimationFrame(animationRef.current);
    };
  }, [nodeCount, growthRate, maxDist, repulsion]);

  const prefersReducedMotion = typeof window !== 'undefined' ? window.matchMedia('(prefers-reduced-motion: reduce)').matches : false;

  if (prefersReducedMotion) {
    return <div style={{width: '100%', height: '100%', minHeight: '500px', display: 'flex', alignItems: 'center', justifyContent: 'center', backgroundColor, color: textColor, fontFamily: 'ui-monospace, monospace', fontSize: `${fontSize}px`, fontWeight}}>Animation disabled (reduced motion preference detected)</div>;
  }

  return (
    <div ref={containerRef} className="differential-growth" style={{width: '100%', height: '100%', minHeight: '500px', position: 'relative', backgroundColor, overflow: 'hidden'}}>
      <canvas ref={canvasRef} style={{display: 'block', width: '100%', height: '100%'}} />
      <div style={{position: 'absolute', bottom: '16px', left: '16px', display: 'flex', gap: '8px'}}>
        <button onClick={togglePause} style={{padding: '8px 16px', backgroundColor: 'rgba(255,255,255,0.9)', border: `1px solid ${textColor}`, borderRadius: '6px', color: textColor, fontFamily: 'ui-monospace, monospace', fontSize: `${fontSize}px`, fontWeight, cursor: 'pointer', transition: 'all 200ms ease-out'}}>{isPausedRef.current ? 'PLAY' : 'PAUSE'}</button>
        <button onClick={handleReset} style={{padding: '8px 16px', backgroundColor: 'rgba(255,255,255,0.9)', border: `1px solid ${textColor}`, borderRadius: '6px', color: textColor, fontFamily: 'ui-monospace, monospace', fontSize: `${fontSize}px`, fontWeight, cursor: 'pointer', transition: 'all 200ms ease-out'}}>RESET</button>
      </div>
      {showInfo && (
        <div style={{position: 'absolute', top: '16px', right: '16px', padding: '12px 16px', backgroundColor: 'rgba(255,255,255,0.9)', border: `1px solid ${textColor}`, borderRadius: '6px', fontFamily: 'ui-monospace, monospace', fontSize: `${fontSize}px`, fontWeight, color: textColor, lineHeight: 1.6}}>
          <div>NODES: {nodesRef.current.length}</div>
          <div>GROWTH: {growthRate}</div>
          <div>REPULSION: {repulsion}</div>
        </div>
      )}
    </div>
  );
}

