function Component({ config = {} }) {
  const containerRef = React.useRef(null);
  const canvasRef = React.useRef(null);
  const blobsRef = React.useRef([]);
  const animationRef = React.useRef(null);
  const isPausedRef = React.useRef(false);

  // Safe config access
  const blobCount = parseInt(config?.blobCount || '8');
  const blobSpeed = parseFloat(config?.blobSpeed || '0.5');
  const threshold = parseFloat(config?.threshold || '1.0');
  const showField = config?.showField === true;
  const showInfo = config?.showInfo !== false;
  const resolution = parseInt(config?.resolution || '20');
  const backgroundColor = config?.backgroundColor || '#FFFFFF';
  const blobColor = config?.blobColor || '#212529';
  const outlineColor = config?.outlineColor || '#495057';
  const fieldColor = config?.fieldColor || '#E9ECEF';
  const textColor = config?.textColor || '#495057';
  const lineWidth = parseFloat(config?.lineWidth || '2');
  const fontSize = config?.fontSize || 12;
  const fontWeight = config?.fontWeight || '400';

  // Initialize blobs
  const initializeBlobs = (width, height) => {
    const blobs = [];
    for (let i = 0; i < blobCount; i++) {
      blobs.push({
        x: Math.random() * width,
        y: Math.random() * height,
        vx: (Math.random() - 0.5) * 2 * blobSpeed,
        vy: (Math.random() - 0.5) * 2 * blobSpeed,
        radius: 40 + Math.random() * 60
      });
    }
    return blobs;
  };

  // Update blob positions
  const updateBlobs = (blobs, width, height) => {
    blobs.forEach(blob => {
      blob.x += blob.vx;
      blob.y += blob.vy;

      // Bounce off walls
      if (blob.x - blob.radius < 0 || blob.x + blob.radius > width) {
        blob.vx *= -1;
        blob.x = Math.max(blob.radius, Math.min(width - blob.radius, blob.x));
      }
      if (blob.y - blob.radius < 0 || blob.y + blob.radius > height) {
        blob.vy *= -1;
        blob.y = Math.max(blob.radius, Math.min(height - blob.radius, blob.y));
      }
    });
  };

  // Calculate metaball field value at point
  const getFieldValue = (x, y, blobs) => {
    let sum = 0;
    blobs.forEach(blob => {
      const dx = x - blob.x;
      const dy = y - blob.y;
      const distSq = dx * dx + dy * dy;
      if (distSq > 0) {
        sum += (blob.radius * blob.radius) / distSq;
      }
    });
    return sum;
  };

  // Marching squares lookup table
  const getMarchingSquaresLines = (a, b, c, d, threshold) => {
    const state = (a > threshold ? 8 : 0) |
                  (b > threshold ? 4 : 0) |
                  (c > threshold ? 2 : 0) |
                  (d > threshold ? 1 : 0);

    // Linear interpolation for edge positions
    const lerp = (v1, v2, threshold, val1, val2) => {
      if (Math.abs(val2 - val1) < 0.00001) return 0.5;
      return (threshold - val1) / (val2 - val1);
    };

    const lines = [];

    switch(state) {
      case 1: lines.push([[0.5, 1], [1, 0.5]]); break;
      case 2: lines.push([[0, 0.5], [0.5, 1]]); break;
      case 3: lines.push([[0, 0.5], [1, 0.5]]); break;
      case 4: lines.push([[0.5, 0], [1, 0.5]]); break;
      case 5: lines.push([[0.5, 0], [0.5, 1]]); break;
      case 6: lines.push([[0, 0.5], [0.5, 0]], [[0.5, 1], [1, 0.5]]); break;
      case 7: lines.push([[0, 0.5], [0.5, 0]]); break;
      case 8: lines.push([[0, 0.5], [0.5, 0]]); break;
      case 9: lines.push([[0.5, 1], [0.5, 0]], [[0, 0.5], [1, 0.5]]); break;
      case 10: lines.push([[0.5, 0], [0.5, 1]]); break;
      case 11: lines.push([[0.5, 0], [1, 0.5]]); break;
      case 12: lines.push([[0, 0.5], [1, 0.5]]); break;
      case 13: lines.push([[0, 0.5], [0.5, 1]]); break;
      case 14: lines.push([[0.5, 1], [1, 0.5]]); break;
    }

    return lines;
  };

  // Render function
  const render = (canvas, blobs) => {
    if (!canvas) return;
    const ctx = canvas.getContext('2d');
    const width = canvas.width;
    const height = canvas.height;

    // Clear
    ctx.fillStyle = backgroundColor;
    ctx.fillRect(0, 0, width, height);

    const cellSize = resolution;
    const cols = Math.ceil(width / cellSize);
    const rows = Math.ceil(height / cellSize);

    // Calculate field
    const field = [];
    for (let j = 0; j <= rows; j++) {
      field[j] = [];
      for (let i = 0; i <= cols; i++) {
        const x = i * cellSize;
        const y = j * cellSize;
        field[j][i] = getFieldValue(x, y, blobs);
      }
    }

    // Draw field visualization if enabled
    if (showField) {
      ctx.fillStyle = fieldColor;
      for (let j = 0; j < rows; j++) {
        for (let i = 0; i < cols; i++) {
          const value = field[j][i];
          const alpha = Math.min(value / threshold, 1) * 0.3;
          ctx.globalAlpha = alpha;
          ctx.fillRect(i * cellSize, j * cellSize, cellSize, cellSize);
        }
      }
      ctx.globalAlpha = 1;
    }

    // Draw metaball contours using marching squares
    ctx.strokeStyle = outlineColor;
    ctx.lineWidth = lineWidth;
    ctx.lineCap = 'round';
    ctx.lineJoin = 'round';

    // First pass: draw outlines
    for (let j = 0; j < rows; j++) {
      for (let i = 0; i < cols; i++) {
        const x = i * cellSize;
        const y = j * cellSize;

        const a = field[j][i];
        const b = field[j][i + 1];
        const c = field[j + 1][i + 1];
        const d = field[j + 1][i];

        const lines = getMarchingSquaresLines(a, b, c, d, threshold);

        lines.forEach(line => {
          const x1 = x + line[0][0] * cellSize;
          const y1 = y + line[0][1] * cellSize;
          const x2 = x + line[1][0] * cellSize;
          const y2 = y + line[1][1] * cellSize;

          ctx.beginPath();
          ctx.moveTo(x1, y1);
          ctx.lineTo(x2, y2);
          ctx.stroke();
        });
      }
    }

    // Second pass: fill blob areas
    ctx.fillStyle = blobColor;
    for (let j = 0; j < rows; j++) {
      for (let i = 0; i < cols; i++) {
        const x = i * cellSize;
        const y = j * cellSize;
        const centerValue = getFieldValue(x + cellSize/2, y + cellSize/2, blobs);

        if (centerValue > threshold) {
          ctx.globalAlpha = 0.15;
          ctx.fillRect(x, y, cellSize, cellSize);
        }
      }
    }
    ctx.globalAlpha = 1;
  };

  // Animation loop
  const animate = () => {
    if (isPausedRef.current) return;

    const canvas = canvasRef.current;
    if (!canvas) return;

    updateBlobs(blobsRef.current, canvas.width, canvas.height);
    render(canvas, blobsRef.current);

    animationRef.current = requestAnimationFrame(animate);
  };

  // Toggle pause
  const togglePause = () => {
    isPausedRef.current = !isPausedRef.current;
    if (!isPausedRef.current) {
      animate();
    }
  };

  // Reset
  const handleReset = () => {
    const canvas = canvasRef.current;
    if (canvas) {
      blobsRef.current = initializeBlobs(canvas.width, canvas.height);
      render(canvas, blobsRef.current);
    }
  };

  // Initialize
  React.useEffect(() => {
    const canvas = canvasRef.current;
    const container = containerRef.current;
    if (!canvas || !container) return;

    const updateSize = () => {
      const rect = container.getBoundingClientRect();
      if (rect.width > 0 && rect.height > 0) {
        canvas.width = rect.width;
        canvas.height = rect.height;
        blobsRef.current = initializeBlobs(rect.width, rect.height);
      }
    };

    updateSize();

    const resizeObserver = new ResizeObserver(updateSize);
    resizeObserver.observe(container);

    animate();

    return () => {
      resizeObserver.disconnect();
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, [blobCount, blobSpeed, threshold, resolution]);

  // prefers-reduced-motion
  const prefersReducedMotion = typeof window !== 'undefined' 
    ? window.matchMedia('(prefers-reduced-motion: reduce)').matches 
    : false;

  if (prefersReducedMotion) {
    return (
      <div style={{
        width: '100%',
        height: '100%',
        minHeight: '500px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor,
        color: textColor,
        fontFamily: 'system-ui, -apple-system, sans-serif',
        fontSize: `${fontSize}px`,
        fontWeight
      }}>
        Animation disabled (reduced motion preference detected)
      </div>
    );
  }

  return (
    <div 
      ref={containerRef}
      className="metaballs" 
      style={{
        width: '100%',
        height: '100%',
        minHeight: '500px',
        position: 'relative',
        backgroundColor,
        overflow: 'hidden'
      }}
    >
      <canvas
        ref={canvasRef}
        style={{
          display: 'block',
          width: '100%',
          height: '100%'
        }}
      />

      {/* Controls */}
      <div style={{
        position: 'absolute',
        bottom: '16px',
        left: '16px',
        display: 'flex',
        gap: '8px'
      }}>
        <button
          onClick={togglePause}
          style={{
            padding: '8px 16px',
            backgroundColor: 'rgba(255,255,255,0.9)',
            border: `1px solid ${outlineColor}`,
            borderRadius: '6px',
            color: textColor,
            fontFamily: 'system-ui, -apple-system, sans-serif',
            fontSize: `${fontSize}px`,
            fontWeight,
            cursor: 'pointer',
            transition: 'all 200ms ease-out'
          }}
        >
          {isPausedRef.current ? 'PLAY' : 'PAUSE'}
        </button>

        <button
          onClick={handleReset}
          style={{
            padding: '8px 16px',
            backgroundColor: 'rgba(255,255,255,0.9)',
            border: `1px solid ${outlineColor}`,
            borderRadius: '6px',
            color: textColor,
            fontFamily: 'system-ui, -apple-system, sans-serif',
            fontSize: `${fontSize}px`,
            fontWeight,
            cursor: 'pointer',
            transition: 'all 200ms ease-out'
          }}
        >
          RESET
        </button>
      </div>

      {/* Info */}
      {showInfo && (
        <div style={{
          position: 'absolute',
          top: '16px',
          right: '16px',
          padding: '12px 16px',
          backgroundColor: 'rgba(255,255,255,0.9)',
          border: `1px solid ${outlineColor}`,
          borderRadius: '6px',
          fontFamily: 'system-ui, -apple-system, sans-serif',
          fontSize: `${fontSize}px`,
          fontWeight,
          color: textColor,
          lineHeight: 1.6
        }}>
          <div>BLOBS: {blobCount}</div>
          <div>THRESHOLD: {threshold}</div>
          <div>RESOLUTION: {resolution}px</div>
        </div>
      )}
    </div>
  );
}

const MANIFEST = {"type": "Visualization.NavierStokesFluid", "description": "Real-time fluid dynamics with vorticity and pressure simulation", "editorElement": {"selector": ".navier-stokes-fluid", "displayName": "Navier-Stokes Fluid", "archetype": "container", "data": {"gridSize": {"dataType": "select", "displayName": "Grid Resolution", "defaultValue": "80", "options": ["50", "60", "80", "100"], "group": "Content"}, "viscosity": {"dataType": "select", "displayName": "Viscosity", "defaultValue": "0.0001", "options": ["0.00001", "0.0001", "0.001", "0.01"], "group": "Animation"}, "diffusion": {"dataType": "select", "displayName": "Diffusion", "defaultValue": "0.00001", "options": ["0.00001", "0.0001", "0.001"], "group": "Animation"}, "showVelocity": {"dataType": "booleanValue", "displayName": "Show Velocity Field", "defaultValue": true, "group": "Content"}, "showInfo": {"dataType": "booleanValue", "displayName": "Show Info", "defaultValue": true, "group": "Content"}, "backgroundColor": {"dataType": "color", "displayName": "Background", "defaultValue": "#FFFFFF", "group": "Colors"}, "fluidColor": {"dataType": "color", "displayName": "Fluid Color", "defaultValue": "#212529", "group": "Colors"}, "velocityColor": {"dataType": "color", "displayName": "Velocity Color", "defaultValue": "#495057", "group": "Colors"}, "textColor": {"dataType": "color", "displayName": "Text Color", "defaultValue": "#495057", "group": "Colors"}, "fontSize": {"dataType": "number", "displayName": "Font Size", "defaultValue": 12, "group": "Typography"}, "fontWeight": {"dataType": "select", "displayName": "Font Weight", "defaultValue": "400", "options": ["300", "400", "500"], "group": "Typography"}}, "layout": {"resizeDirection": "horizontalAndVertical", "contentResizeDirection": "vertical"}}};

function Component({ config = {} }) {
  const containerRef = React.useRef(null);
  const canvasRef = React.useRef(null);
  const fluidRef = React.useRef({u: [], v: [], density: []});
  const animationRef = React.useRef(null);
  const isPausedRef = React.useRef(false);
  const mouseRef = React.useRef({x: 0, y: 0, isDown: false});

  const gridSize = parseInt(config?.gridSize || '80');
  const viscosity = parseFloat(config?.viscosity || '0.0001');
  const diffusion = parseFloat(config?.diffusion || '0.00001');
  const showVelocity = config?.showVelocity !== false;
  const showInfo = config?.showInfo !== false;
  const backgroundColor = config?.backgroundColor || '#FFFFFF';
  const fluidColor = config?.fluidColor || '#212529';
  const velocityColor = config?.velocityColor || '#495057';
  const textColor = config?.textColor || '#495057';
  const fontSize = config?.fontSize || 12;
  const fontWeight = config?.fontWeight || '400';

  const initializeFluid = () => {
    const u = [], v = [], density = [];
    for (let i = 0; i < gridSize; i++) {
      u[i] = [];
      v[i] = [];
      density[i] = [];
      for (let j = 0; j < gridSize; j++) {
        u[i][j] = 0;
        v[i][j] = 0;
        density[i][j] = 0;
      }
    }
    return {u, v, density};
  };

  const diffuse = (x, x0, diff, dt, N) => {
    const a = dt * diff * N * N;
    for (let k = 0; k < 4; k++) {
      for (let i = 1; i < N - 1; i++) {
        for (let j = 1; j < N - 1; j++) {
          x[i][j] = (x0[i][j] + a * (x[i-1][j] + x[i+1][j] + x[i][j-1] + x[i][j+1])) / (1 + 4 * a);
        }
      }
    }
  };

  const advect = (d, d0, u, v, dt, N) => {
    for (let i = 1; i < N - 1; i++) {
      for (let j = 1; j < N - 1; j++) {
        let x = i - dt * N * u[i][j];
        let y = j - dt * N * v[i][j];

        x = Math.max(0.5, Math.min(N - 1.5, x));
        y = Math.max(0.5, Math.min(N - 1.5, y));

        const i0 = Math.floor(x);
        const j0 = Math.floor(y);
        const i1 = i0 + 1;
        const j1 = j0 + 1;

        const s1 = x - i0;
        const s0 = 1 - s1;
        const t1 = y - j0;
        const t0 = 1 - t1;

        d[i][j] = s0 * (t0 * d0[i0][j0] + t1 * d0[i0][j1]) +
                  s1 * (t0 * d0[i1][j0] + t1 * d0[i1][j1]);
      }
    }
  };

  const step = (fluid) => {
    const dt = 0.1;
    const N = gridSize;

    const u0 = fluid.u.map(row => [...row]);
    const v0 = fluid.v.map(row => [...row]);
    const dens0 = fluid.density.map(row => [...row]);

    diffuse(u0, fluid.u, viscosity, dt, N);
    diffuse(v0, fluid.v, viscosity, dt, N);
    advect(fluid.u, u0, u0, v0, dt, N);
    advect(fluid.v, v0, u0, v0, dt, N);

    diffuse(dens0, fluid.density, diffusion, dt, N);
    advect(fluid.density, dens0, fluid.u, fluid.v, dt, N);

    for (let i = 0; i < N; i++) {
      for (let j = 0; j < N; j++) {
        fluid.density[i][j] *= 0.99;
      }
    }
  };

  const render = (canvas, fluid) => {
    if (!canvas) return;
    const ctx = canvas.getContext('2d');
    const width = canvas.width;
    const height = canvas.height;

    ctx.fillStyle = backgroundColor;
    ctx.fillRect(0, 0, width, height);

    const cellW = width / gridSize;
    const cellH = height / gridSize;

    for (let i = 0; i < gridSize; i++) {
      for (let j = 0; j < gridSize; j++) {
        const dens = fluid.density[i][j];
        if (dens > 0.01) {
          ctx.fillStyle = fluidColor;
          ctx.globalAlpha = Math.min(dens * 10, 1);
          ctx.fillRect(i * cellW, j * cellH, cellW + 1, cellH + 1);
        }
      }
    }
    ctx.globalAlpha = 1;

    if (showVelocity) {
      ctx.strokeStyle = velocityColor;
      ctx.lineWidth = 0.5;
      const step = 5;
      for (let i = 0; i < gridSize; i += step) {
        for (let j = 0; j < gridSize; j += step) {
          const u = fluid.u[i][j];
          const v = fluid.v[i][j];
          if (Math.abs(u) + Math.abs(v) > 0.1) {
            const x = i * cellW + cellW / 2;
            const y = j * cellH + cellH / 2;
            ctx.beginPath();
            ctx.moveTo(x, y);
            ctx.lineTo(x + u * 50, y + v * 50);
            ctx.stroke();
          }
        }
      }
    }
  };

  const animate = () => {
    if (isPausedRef.current) return;
    const canvas = canvasRef.current;
    if (!canvas) return;
    step(fluidRef.current);
    render(canvas, fluidRef.current);
    animationRef.current = requestAnimationFrame(animate);
  };

  const handleMouseMove = (e) => {
    const canvas = canvasRef.current;
    if (!canvas) return;
    const rect = canvas.getBoundingClientRect();
    const x = ((e.clientX - rect.left) / rect.width) * gridSize;
    const y = ((e.clientY - rect.top) / rect.height) * gridSize;

    if (mouseRef.current.isDown) {
      const i = Math.floor(x);
      const j = Math.floor(y);
      if (i > 0 && i < gridSize - 1 && j > 0 && j < gridSize - 1) {
        const dx = x - mouseRef.current.x;
        const dy = y - mouseRef.current.y;
        fluidRef.current.u[i][j] += dx * 50;
        fluidRef.current.v[i][j] += dy * 50;
        fluidRef.current.density[i][j] += 100;
      }
    }
    mouseRef.current.x = x;
    mouseRef.current.y = y;
  };

  const togglePause = () => {
    isPausedRef.current = !isPausedRef.current;
    if (!isPausedRef.current) animate();
  };

  const handleReset = () => {
    fluidRef.current = initializeFluid();
    if (canvasRef.current) render(canvasRef.current, fluidRef.current);
  };

  React.useEffect(() => {
    const canvas = canvasRef.current;
    const container = containerRef.current;
    if (!canvas || !container) return;

    const updateSize = () => {
      const rect = container.getBoundingClientRect();
      if (rect.width > 0 && rect.height > 0) {
        canvas.width = rect.width;
        canvas.height = rect.height;
        fluidRef.current = initializeFluid();
      }
    };

    updateSize();
    const resizeObserver = new ResizeObserver(updateSize);
    resizeObserver.observe(container);

    canvas.addEventListener('mousemove', handleMouseMove);
    canvas.addEventListener('mousedown', () => mouseRef.current.isDown = true);
    canvas.addEventListener('mouseup', () => mouseRef.current.isDown = false);
    canvas.addEventListener('mouseleave', () => mouseRef.current.isDown = false);

    animate();

    return () => {
      resizeObserver.disconnect();
      canvas.removeEventListener('mousemove', handleMouseMove);
      if (animationRef.current) cancelAnimationFrame(animationRef.current);
    };
  }, [gridSize, viscosity, diffusion]);

  const prefersReducedMotion = typeof window !== 'undefined' ? window.matchMedia('(prefers-reduced-motion: reduce)').matches : false;

  if (prefersReducedMotion) {
    return <div style={{width: '100%', height: '100%', minHeight: '500px', display: 'flex', alignItems: 'center', justifyContent: 'center', backgroundColor, color: textColor, fontFamily: 'ui-monospace, monospace', fontSize: `${fontSize}px`, fontWeight}}>Animation disabled (reduced motion preference detected)</div>;
  }

  return (
    <div ref={containerRef} className="navier-stokes-fluid" style={{width: '100%', height: '100%', minHeight: '500px', position: 'relative', backgroundColor, overflow: 'hidden', cursor: 'crosshair'}}>
      <canvas ref={canvasRef} style={{display: 'block', width: '100%', height: '100%'}} />
      <div style={{position: 'absolute', bottom: '16px', left: '16px', display: 'flex', gap: '8px'}}>
        <button onClick={togglePause} style={{padding: '8px 16px', backgroundColor: 'rgba(255,255,255,0.9)', border: `1px solid ${textColor}`, borderRadius: '6px', color: textColor, fontFamily: 'ui-monospace, monospace', fontSize: `${fontSize}px`, fontWeight, cursor: 'pointer', transition: 'all 200ms ease-out'}}>{isPausedRef.current ? 'PLAY' : 'PAUSE'}</button>
        <button onClick={handleReset} style={{padding: '8px 16px', backgroundColor: 'rgba(255,255,255,0.9)', border: `1px solid ${textColor}`, borderRadius: '6px', color: textColor, fontFamily: 'ui-monospace, monospace', fontSize: `${fontSize}px`, fontWeight, cursor: 'pointer', transition: 'all 200ms ease-out'}}>RESET</button>
      </div>
      {showInfo && (
        <div style={{position: 'absolute', top: '16px', right: '16px', padding: '12px 16px', backgroundColor: 'rgba(255,255,255,0.9)', border: `1px solid ${textColor}`, borderRadius: '6px', fontFamily: 'ui-monospace, monospace', fontSize: `${fontSize}px`, fontWeight, color: textColor, lineHeight: 1.6}}>
          <div>GRID: {gridSize}x{gridSize}</div>
          <div>VISCOSITY: {viscosity}</div>
          <div>DRAG TO ADD FLUID</div>
        </div>
      )}
    </div>
  );
}

