        "defaultValue": "1.0",
        "options": ["0.5", "0.75", "1.0", "1.5", "2.0"],
        "group": "Animation"
      },
      "autoplay": {
        "dataType": "booleanValue",
        "displayName": "Autoplay on Load",
        "defaultValue": true,
        "group": "Animation"
